package Mypack;

public class DisplayNames {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("Below is coding in Java where character info are displayed");	

	String firstName = "Yared";
	String lastName = "Abegaz";
	String fullName = firstName + " " + lastName;
	
	int age = 25;
	double salary = 45000.45;
	char gender = 'M';
	System.out.println();
	System.out.println("The full name of the person is: " + fullName);
	System.out.println("The age of the person is: " + age);
	System.out.println();
	System.out.println("The salary amount of the person is: " + salary);
	System.out.println("The gender of the person is: " + gender);
		
	
	}

}
