package Unaryoperators;

public class unaryoperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a = 20;
System.out.println(a++); //print out value of a before increment--20
System.out.println(a); //print out value of a after increment 20 + 1 = 21
System.out.println(++a); //print out value of a after increment 21 + 1 = 22
System.out.println(a--); //print out value of a before decrement--22
System.out.println(a);   //print out value of a after decrement 22 - 1 = 21
System.out.println(--a); //print out value of a after decrement 21 - 1 = 20
	}

}
